mapper.update:
update userinfo
   set UserName = #{userName, jdbcType=VARCHAR},Password = #{password, jdbcType=VARCHAR}, Sex = #{sex, jdbcType=INTEGER},
   Birthday = #{birthday, jdbcType=TIMESTAMP}, IsSystem = #{isSystem, jdbcType=BOOLEAN}, isDisabled = #{isDisabled, jdbcType=BOOLEAN}
 where UserGuid = #{userGuid, jdbcType=VARCHAR}

mapper.select:
select count(case when userCode = #{userCode, jdbcType=VARCHAR} then 1 else null end) as codeCount
  from UserInfo
 where UserGuid <> #{userGuid, jdbcType=VARCHAR}

SELECT userguid,userName,userCode,password FROM UserInfo
WHERE 'a' = #{a, jdbcType=VARCHAR}
  AND 'b' = #{b.userCode, jdbcType=VARCHAR}
